import { Component , Input, Output, EventEmitter} from '@angular/core';
import { Product, products } from '../products';
import {DialogModule} from 'primeng/dialog';
import { CartService } from "../cart.service";


@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
  products = [...products];
  display: boolean = false;

  constructor(private cartService: CartService) {}

  @Input() product!: Product;

  share() {
    window.alert("The product has been shared!");
  }

  onNotify() {
    window.alert("You will be notified when the product goes on sale");
  }

  showDialog() {
    this.display = true;
  }

  addToCart(product: Product) {
    this.cartService.addToCart(product);
    window.alert("Your product has been added to the cart!");
  }
}
<h2>Products</h2>
  <h3>
    <a [title]="product.name + ' details'"
    [routerLink]="['/products', product.id]">
      {{ product.name }}
    </a>
  </h3>
  <p-dialog
  [(visible)]="display" header="{{ product.name }}"[style]="{ width: '50vw' }">

<p *ngIf="product.description">Description: {{ product.description }}</p>
<img
  src="{{ product.images }}"
  width="auto"
  height="215px"
  alt="Sin imagen"
/>
<p>{{ product.price | currency }}</p>
<button (click)="addToCart(product)">Comprar</button>
</p-dialog>

<button class="btn" type="button" (click)="showDialog()" label="Show">
<i class="pi pi-info-circle"></i>Mostrar
</button>

<p *ngIf="product.description">Description: {{ product.description }}</p>

<button type="button" (click)="share()">
  Share
</button>
<pre></pre>

<button type="button" (click)="onNotify()">Notify Me</button>
